(265, 11, 'pg', 'Perhatikan jaring-jaring makanan berikut ini :\n\n<img alt=\\"\\" src=\\"data:image/png;base64,\\" />\n\nApabila belalang dibasmi oleh para petani, maka yang terjadi pada jaring-jaring makanan tersebut adalah …', '["populasi ular meningkat","populasi ayam menurun","populasi katak meningkat","populasi tikus meningkat"]', 'b', '{"tipe":"pg"}'),
(266, 11, 'pg', '<img alt=\\"\\" src=\\"\\">', '["mengecilkan cerobong asap\\r\\n","melakukan uji emisi gas buang","membuat instalasi pembuangan gas yang baik","menggantikan bahan bakar batubara dengan minyak"]', 'c', '{"tipe":"pg"}');
